import React, {Component} from 'react';
import './App.css';
import NavigationBar  from './components/Navigation';
import ImageLink  from './components/ImageLink';
import Rank from './components/Rank';
import FaceRecognition from './components/FaceRecognition';
import 'tachyons';
import Clarifai from 'clarifai';
const AI = new Clarifai.App({apiKey: 'c94246448a1b46a89010cd39ad198241'});

class App extends Component {
  constructor(){
    super();
    this.state = {
      input: '',
      imageUrl: '',
      namesList: [],
      faceRec: false,
      Boxes: {}
    }
  }

  calculateFaceLocation = (data)=>{
    let boxList = [];
    for(let i = 0; i < data.outputs[0].data.regions.length;i++){
      boxList.push(data.outputs[0].data.regions[i].region_info.bounding_box);
    }
    const image = document.getElementById('inputimage');
    console.log(image.width , image.height);

  }
  onImageLinkChange = ( event ) => {
    this.setState({input: event.target.value});
  }
  onSubmit =() =>{
    this.setState({imageUrl: this.state.input})
    AI.models.initModel({id: Clarifai.GENERAL_MODEL, version: "aa7f35c01e0642fda5cf400f543e7c40"})
      .then(generalModel => {
        return generalModel.predict(this.state.input);
      })
      .then(response => {
        var concepts = response['outputs'][0]['data']['concepts'];
        let returnObjects = [];
        let face = 0;
        for(var i = 0; i< concepts.length;i++){
          returnObjects.push(concepts[i]);

          if(concepts[i].name === "face" || concepts[i].name === "man" || concepts[i].name === "woman" ){
             face = face + 1;
          }
          if(face > 0){
            this.setState({faceRec: true})
          }
          else if(face === 0){
            this.setState({faceRec: false})
          }
        }
        this.setState({namesList: returnObjects})
        return(face);
      }).then(face => {
        console.log(face);
        if(face > 0){
          AI.models.predict("a403429f2ddf4b49b307e318f00e528b", this.state.input).then(response => {
            console.log(response);  
            this.calculateFaceLocation(response);

              // let returnObs = {};
              // for(var i = 0; i< concepts.length;i++){
              //   returnObs.push(concepts[i]);

              //   }

                // do something with response
            },
            (err => {console.log(err)})
          );
        }
      });



  }
  render(){
    return (
      <div className="App">
        <NavigationBar />
        <Rank />
        <ImageLink onImageLinkChange={this.onImageLinkChange} 
                    onSubmit={this.onSubmit}/>
        <FaceRecognition imageUrl={this.state.imageUrl} namesList={this.state.namesList} faceRec={this.state.faceRec}/>

      </div>
    );
  }
}

export default App;
